package com.finogeeks.uniplugins_mopsdk;import android.content.Context;import com.finogeeks.lib.applet.api.AppletApi;import com.finogeeks.lib.applet.interfaces.ICallback;import org.jetbrains.annotations.NotNull;import org.json.JSONObject;import java.util.UUID;import io.dcloud.feature.uniapp.bridge.UniJSCallback;public class MopSdkExtensionApi extends AppletApi {    private final String apiName;    private final UniJSCallback handler;    public MopSdkExtensionApi(Context context, String apiName, UniJSCallback handler) {        super(context);        this.apiName = apiName;        this.handler = handler;    }    @Override    public void invoke(@NotNull String appId,                       @NotNull String event,                       @NotNull JSONObject jsonObject,                       @NotNull ICallback iCallback) {        if (this.handler != null) {            com.alibaba.fastjson.JSONObject options = new com.alibaba.fastjson.JSONObject();            String uuid = MopSdkModule.addExtensionCallBack(iCallback);            options.put("uuid", uuid);            options.put("appId", appId);            options.put("event", event);            options.put("params", jsonObject);            this.handler.invokeAndKeepAlive(options);        }    }    @Override    public String[] apis() {        return new String[]{this.apiName};    }}